import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as a,c as t,a as i}from"./app-1423be03.js";const o={},d=i('<h1 id="mvvm框架" tabindex="-1"><a class="header-anchor" href="#mvvm框架" aria-hidden="true">#</a> MVVM框架</h1><h2 id="什么是mvvm框架" tabindex="-1"><a class="header-anchor" href="#什么是mvvm框架" aria-hidden="true">#</a> 什么是MVVM框架</h2><p>MVVM框架指的是 Model（模型）、View（视图）、View Model（视图模型）的架构模式。</p><p>View改变流程：用户输入 --&gt; View --&gt; ViewModel --&gt; Model</p><p>Model改变流程: Ajax请求--&gt; Model --&gt; ViewModel --&gt; View</p><h2 id="什么是mvc框架" tabindex="-1"><a class="header-anchor" href="#什么是mvc框架" aria-hidden="true">#</a> 什么是MVC框架</h2><p>MVC框架指的是Model、View、Controller的架构模式。</p><p>View流程：用户输入 --&gt; View ---&gt; Controller ---&gt; Model ---&gt;View</p><p>Model改变流程: Ajax请求--&gt; Model --&gt; Controller ---&gt; View</p><h2 id="mvvm对mvc框架的优化" tabindex="-1"><a class="header-anchor" href="#mvvm对mvc框架的优化" aria-hidden="true">#</a> MVVM对MVC框架的优化</h2><ul><li><p>在ViewModel中通过数据绑定和DOM事件监听，将View和Model层进行了解耦。利于两者的复用。</p></li><li><p>解决了MVC中大量DOM操作提升渲染性能。</p></li></ul>',11),r=[d];function l(M,V){return a(),t("div",null,r)}const n=e(o,[["render",l],["__file","MVVM框架.html.vue"]]);export{n as default};
